settings.custom_css:
@-webkit-keyframes in {
    0%   { -webkit-transform: translate(0%,0%); }
    20%   { 
        -webkit-filter: opacity(0);
        -webkit-transform: translate(-50%,0%); }
    40%   { 
        -webkit-filter: opacity(0);
        -webkit-transform: translate(-50%,50%); }
    60%   {
        -webkit-filter: opacity(0);
        -webkit-transform: translate(50%,50%); }
    80%   { 
        -webkit-filter: opacity(0);
        -webkit-transform: translate(50%,0); }
    100%   { -webkit-transform: translate(0,0); }
}

@-webkit-keyframes out {
    0%   { -webkit-transform: translate(0,0); }
    20%   { 
        -webkit-filter: opacity(0);
        -webkit-transform: translate(50%,0); }
    40%   {
        -webkit-filter: opacity(0);
        -webkit-transform: translate(50%,50%); }
    60%   { 
        -webkit-filter: opacity(0);
        -webkit-transform: translate(-50%,50%); }
    80%   { 
        -webkit-filter: opacity(0);
        -webkit-transform: translate(-50%,0%); }
    100%   { -webkit-transform: translate(0%,0%); }
}

